import React, { Component } from 'react'
import NewsItem from './NewsItem'
import Spinner from './Spinner';
import PropTypes from 'prop-types'
import InfiniteScroll from 'react-infinite-scroll-component';


export class News extends Component {
    capitalizeFirstLetter(string){
        return string.charAt(0).toUpperCase()+string.slice(1);
    }
   
    constructor(props){
        super(props);
        this.state={
            articles:[],
            loading:false,
            page:1,
            totalResults:0
        }
        document.title=`NewsMonkey-${this.capitalizeFirstLetter(this.props.category)}`;
    }
    async updateNews(){
        this.setState({loading:true})
        let url=`https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=7a62fb1796d84c898f39d25146f0ea69&page=${this.state.page}&pageSize=${this.props.pageSize}`;
        let data= await fetch(url);// it' s a promise the method is wating for the rsponse either failure or success 
        let parsedData= await data.json();
        console.log(parsedData);
        this.setState({articles: parsedData.articles,
            totalResults:parsedData.totalResults,
            loading:false
        })
    }
    async componentDidMount(){// it is lifecycle method/ it runs after render() method
       this.updateNews();

    }
     handleNextClick= async ()=>{
    
    this.setState({page:this.state.page+1});
    this.updateNews()
    }
    handlePreviousClick= async ()=>{
        this.setState({page:this.state.page+1});
         this.updateNews()
    }
    render() {
        
        return (
            <div className="container my-3">
            <h1 className="text-center" style={{margin:'40px'}}>NewsMonkey - Top Headlines from {this.capitalizeFirstLetter(this.props.category)}</h1>
           {/*{ this.state.loading && <Spinner/> }*/} {/* This state that if the loading is true then show spinner  */}
           
           <InfiniteScroll
            dataLength={this.state.articles.length}
            next={this.fetchMoreData}
            style={{ display: 'flex', flexDirection: 'column-reverse' }} //To put endMessage and loader to the top.
            inverse={true} //
            hasMore={this.state.articles.length!==this.totalResults}
            loader={<h4>Loading...</h4>}
            scrollableTarget="scrollableDiv"
        >
                <div className="row ">
                {this.state.articles.map((elements)=>{
                    return <div className="col-md-4" key={elements.url}>
                    <NewsItem title={elements.title?elements.title.slice(0,45):""} description={elements.description?elements.description.slice(0,88):""} 
                    imageUrl={elements.urlToImage} newsUrl={elements.url} author={elements.author} date={elements.publishedAt} source={elements.source}/>
                </div>
                })}
                </div>
                </InfiniteScroll>
                <div className="container d-flex justify-content-between" >
                <button type="button" disabled={this.state.page<=1} className="btn btn-dark " onClick={this.handlePreviousClick}> &laquo; Previous</button>
                <button type="button" disabled={this.state.page+1> Math.ceil(this.state.totalResults/ this.props.pageSize)} className="btn btn-dark " onClick={this.handleNextClick}>Next &raquo;</button>
                </div>
           
            
            </div>
            
        )
    }
    static defaultProps={
        country:'in',
        pageSize:8,
        category:'general'
    }
    static propTypes={
        country:PropTypes.string,
        pageSize:PropTypes.number,
        category:PropTypes.string
    }
}

export default News
