import React, { useState , useEffect} from 'react'
import NewsItem from './NewsItem'
import Spinner from './Spinner';
import PropTypes from 'prop-types'
import InfiniteScroll from 'react-infinite-scroll-component';

//* document.title=`NewsMonkey-${this.capitalizeFirstLetter(props.category)}`;
const News=(props)=> {
    const [articles, setArticles] = useState([])
    const [loading, setLoading] = useState(true)
    const [page, setPage] = useState(1)
    const [totalresults, setTotalresults] = useState(initialState);

   const capitalizeFirstLetter =(string)=>{
        return string.charAt(0).toUpperCase()+string.slice(1);
    }
   
    const  updateNews=async ()=>{
        props.setProgress(10)
        this.setState({loading:true})
        let url=`https://newsapi.org/v2/top-headlines?country=${props.country}&category=${props.category}&apiKey=${props.apiKey}&page=${this.state.page}&pageSize=${props.pageSize}`;
        let data= await fetch(url);// it' s a promise the method is wating for the rsponse either failure or success 
        let parsedData= await data.json();
        props.setProgress(0);
        setArticles(parsedData.articles);
        setTotalresults(parsedData.totalResults);
        setLoading(false);
        props.setProgress(100);
    }
    useEffect(() => {
        this.updateNews();
    
    }, []);
    // const componentDidMount= async()=>{// it is lifecycle method/ it runs after render() method
    //    updateNews();

    // }
     handleNextClick= async ()=>{
    
    this.setState({page:this.state.page+1});
    this.updateNews()
    }
    handlePreviousClick= async ()=>{
        this.setState({page:this.state.page+1});
         this.updateNews()
    }
    fetchMoreData= async()=>{
        this.setState({page:this.state.page+1});
        let url=`https://newsapi.org/v2/top-headlines?country=${props.country}&category=${props.category}&apiKey=7a62fb1796d84c898f39d25146f0ea69&page=${this.state.page}&pageSize=${props.pageSize}`;
        let data= await fetch(url);// it' s a promise the method is wating for the rsponse either failure or success 
        let parsedData= await data.json();
        this.setState({articles: this.state.articles.concat(parsedData.articles),
            totalResults:parsedData.totalResults,
        })

    }
        
        return (
            <>
            <h1 className="text-center" style={{margin:'40px'}}>NewsMonkey - Top Headlines from {this.capitalizeFirstLetter(props.category)}</h1>
           { this.state.loading && <Spinner/> } {/* This state that if the loading is true then show spinner  */}
           <InfiniteScroll
           dataLength={this.state.articles.length}
           next={this.fetchMoreData}
           hasMore={this.state.articles.length!==this.state.totalResults}
           loader={<Spinner/>}
           scrollableTarget="scrollableDiv"
       >
                <div className="container">
                <div className="row ">
                {this.state.articles.map((elements)=>{
                    return <div className="col-md-4" key={elements.url}>
                    <NewsItem title={elements.title?elements.title.slice(0,45):""} description={elements.description?elements.description.slice(0,88):""} 
                    imageUrl={elements.urlToImage} newsUrl={elements.url} author={elements.author} date={elements.publishedAt} source={elements.source}/>
                </div>
        
                })}
                </div>
                </div>
                </InfiniteScroll>
                {/*<div className="container d-flex justify-content-between" >
                <button type="button" disabled={this.state.page<=1} className="btn btn-dark " onClick={this.handlePreviousClick}> &laquo; Previous</button>
                <button type="button" disabled={this.state.page+1> Math.ceil(this.state.totalResults/ props.pageSize)} className="btn btn-dark " onClick={this.handleNextClick}>Next &raquo;</button>
            </div>*/}
            </>

            
        )
    }
    News.defaultProps={
        country:'in',
        pageSize:8,
        category:'general'
    }
    News.propTypes={
        country:PropTypes.string,
        pageSize:PropTypes.number,
        category:PropTypes.string
    
}

export default News
