import React, { Component } from 'react'

export class NewsItem extends Component {

    render() {
        let {title, description,imageUrl, newsUrl,author,date,source}=this.props;
        return (
            <div className="my-3">
            <div className="card" >
            <img src={imageUrl?imageUrl:"https://images.indianexpress.com/2021/10/27-1.jpg"} className="card-img-top" alt="..."/>
            <div className="card-body">
              <h5 className="card-title">{title}<span class="position-absolute top-0 start-100 translate-middle badge rounded-pill bg-dark">
              {source.name}
              <span class="visually-hidden">unread messages</span>
            </span></h5>
              <p className="card-text">{description}.........</p>
              <p className="card-text"><small className="text-muted">By {author===null?"Unknown":author} On {new Date(date).toGMTString()}</small></p>
              <a rel="noreferrer" href={newsUrl} target="_blank" className="btn btn-sm btn-dark">Read More</a>
            </div>
          </div>
            </div>
        )
    }
}

export default NewsItem
